    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        // Calculate crust price
        this.lblPrice.setText(String.valueOf(getCrustPrice(this.radSmall.isSelected(),
                                            this.radMedium.isSelected(), 
                                            this.radLarge.isSelected())+ getToppingPrice()));
        
    }//GEN-LAST:event_btnCalculateActionPerformed

    private void btnClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClearActionPerformed
        // Clear the form
        //default to small crust size
        this.radSmall.setSelected(true);
        
        //clear toppings
        this.chkPepperoni.setSelected(false);
        this.chkBacon.setSelected(false);
        this.chkSausage.setSelected(false);
        this.chkOlives.setSelected(false);
        this.chkShrooms.setSelected(false);
        this.chkOnions.setSelected(false);
        
        // reset price to zero
        resetPrice();
    }//GEN-LAST:event_btnClearActionPerformed

    private void radSmallActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_radSmallActionPerformed
        //reset price to zero
        resetPrice();
        
        //call calc button
        this.btnCalculate.doClick();
    }//GEN-LAST:event_radSmallActionPerformed

