    private void resetPrice()
    {
        this.lblPrice.setText("0");
    }
    
    private double getCrustPrice(boolean small, boolean medium, boolean large)
    {
        //initialize variable
        double dblPrice = 0;
        
        //if statements go here
        if (small)
        {
            dblPrice += 6.99;
        }
        else if (medium)
        {
            dblPrice += 10.99;
        }
        else if (large)
        {
            dblPrice += 16.99;
        }
        
        //return price
        return dblPrice;
    }
    
    private double getToppingPrice()
    {
        //initialize variables
        double dblPrice = 0;
        
        //add toppings
        if(this.chkPepperoni.isSelected())
        {
            dblPrice += 1.49;
        }
        if(this.chkBacon.isSelected())
        {
            dblPrice += 1.49;
        }
        if(this.chkSausage.isSelected())
        {
            dblPrice += 1.49;
        }
        if(this.chkOlives.isSelected())
        {
            dblPrice += 0.99;
        }
        if(this.chkShrooms.isSelected())
        {
            dblPrice += 0.99;
        }
        if(this.chkOnions.isSelected())
        {
            dblPrice += 0.99;
        }
        //return price
        return dblPrice;
    }
